import pandas as pd

# Load the patient note
patient_note = pd.read_csv('patient_note.csv')

# Load the clinical trial inclusion and exclusion criteria
clinical_trial = pd.read_csv('clinical_trial.csv')

# Check eligibility one-by-one for the inclusion criteria
for column in clinical_trial['Inclusion Criteria']:
    if patient_note[column] == 'Not Relevant for Clinical Trial':
        print(f'{column} is Not Relevant for Clinical Trial')
    elif patient_note[column] == 'Eligible':
        print(f'{column} is Eligible')
    else:
        print(f'{column} is Excluded')

# Check eligibility one-by-one for the exclusion criteria
for column in clinical_trial['Exclusion Criteria']:
    if patient_note[column] == 'Not Relevant for Clinical Trial':
        print(f'{column} is Not Relevant for Clinical Trial')
    elif patient_note[column] == 'Eligible':
        print(f'{column} is Eligible')
    else:
        print(f'{column} is Excluded')

# Provide a final summary
print(f'Patient is {("Eligible" if all(patient_note[column] == "Eligible" for column in clinical_trial['Inclusion Criteria']) else "Excluded")} for the clinical trial')
