package clinical trial assistant

import (
	"fmt"
	"strings"
)

func evaluatePatientEligibility(patientNote string, inclusionCriteria string, exclusionCriteria string) string {
	// Tokenize the patient note and inclusion criteria
	patientTokens := strings.Split(patientNote, " ")
	inclusionTokens := strings.Split(inclusionCriteria, " ")

	// Filter out tokens that are not relevant to the clinical trial
	patientFilters := []string{"obese", "PCO", "sleep apnea", "body shape", "pregnant", "smoke", "drugs"}
	inclusionFilters := []string{"mindfulness", "meditation", "pregnancy outcomes"}
	patientRelevant := func(token string) bool {
		for _, filter := range patientFilters {
			if filter == token {
				return false
			}
		}
		return true
	}
	inclusionRelevant := func(token string) bool {
		for _, filter := range inclusionFilters {
			if filter == token {
				return true
			}
		}
		return false
	}
	patientTokens = []string{token for token in patientTokens if patientRelevant(token)}
	inclusionTokens = []string{token for token in inclusionTokens if inclusionRelevant(token)}

	// Check if the patient is eligible for the clinical trial
	patientEligible := true
	for _, token := range patientTokens {
		if !inclusionRelevant(token) {
			patientEligible = false
			break
		}
	}

	// Determine if the patient is eligible for the clinical trial
	if patientEligible {
		return "A: eligible"
	} else {
		return "B: excluded"
	}
}

